#Here we gooooimport tkinter as tkimport turtlefrom tkinter import ttk# Initiating the windowroot = tk.Tk()# making it so the window is not resizableroot.resizable(width=True, height=True)# This sets the size of the windowroot.geometry('800x600')root.configure(bg='gray')# Creating a 1X2 grid matrix for the main windowsroot.grid_rowconfigure(0, weight=1)root.grid_rowconfigure(1, weight=1)root.grid_columnconfigure(0, weight=1)root.grid_columnconfigure(1, weight=1)#Creating the canvas frame and placing it on the left side of the window (first column)canvas_frame = tk.Frame(root, width=400, height=600)canvas_frame.grid(column=0,row=0, rowspan=2, sticky='NESW', padx=5, pady=5)#Creating the interaction frame and placing it in the right window (second column)interaction_frame =tk.Frame(root, width=400, height=300)interaction_frame.grid(column=1,row=0, sticky='NESW', padx=5, pady=5)#Creating the table frame where sata will be storedtable_frame =tk.Frame(root, width=400, height=300)table_frame.grid(column=1,row=1, sticky='NESW', padx=5, pady=5)# Creating a canvas, placing it in the frame and set background colorcanvas = tk.Canvas(canvas_frame, bg = 'white', width= 400, height = 600)canvas.grid(column=0,row=0, sticky='NESW')#configuring the interation frameinteraction_frame.grid_columnconfigure(0, weight =1)interaction_frame.grid_columnconfigure(1, weight =1)interaction_frame.grid_columnconfigure(2, weight =1)interaction_frame.grid_columnconfigure(3, weight =1)interaction_frame.grid_columnconfigure(4, weight =1)interaction_frame.grid_rowconfigure(0, weight =1)interaction_frame.grid_rowconfigure(1, weight =1)interaction_frame.grid_rowconfigure(2, weight =1)interaction_frame.grid_rowconfigure(3, weight =1)interaction_frame.grid_rowconfigure(4, weight =1)interaction_frame.grid_rowconfigure(5, weight =1)interaction_frame.grid_rowconfigure(6, weight =1)def add_part():    pass#Creating buttonbutton_add = tk.Button(interaction_frame,                         text = "Add Part",                          highlightbackground='gray',                         command=add_part)#placing button on canvasbutton_add.grid(column=2,row=9, sticky='NESW', padx=5, pady=5)#Creating Material Drop Down Boxmaterial = tk.StringVar()material_chosen = ttk.Combobox(interaction_frame,                                state="readonly",                               width = 20,                             textvariable = material,                            justify = 'center'                            )material_chosen.grid(column=1,row=1, sticky='NESW', padx=5, pady=5, columnspan=3)  #Material drop down listmaterial_chosen['values'] = (' Ceramic',                           ' Kovar',                          ' Stainless Steel',                          ' Monel'                          )material_chosen.current(0)#Creating text boxesid_text_box = tk.Text(interaction_frame, height = .1, width = 10)id_text_box.grid(column=0,row=6, sticky='', padx=5, pady=5)idtol_text_box = tk.Text(interaction_frame, height = .1, width = 10)idtol_text_box.grid(column=1,row=6, sticky='', padx=5, pady=5)od_text_box = tk.Text(interaction_frame, height = .1, width = 10)od_text_box.grid(column=2,row=6, sticky='', padx=5, pady=5)odtol_text_box = tk.Text(interaction_frame, height = .1, width = 10)odtol_text_box.grid(column=3,row=6, sticky='', padx=5, pady=5)runout_text_box = tk.Text(interaction_frame, height = .1, width = 10)runout_text_box.grid(column=4,row=6, sticky='', padx=5, pady=5)#labeling textboxsid_text_box_label = tk.Label(interaction_frame, text = 'ID')id_text_box_label.grid(column=0,row=5, sticky='', padx=5, pady=5)idtol_text_box_label = tk.Label(interaction_frame, text = 'ID Tolerance')idtol_text_box_label.grid(column=1,row=5, sticky='', padx=5, pady=5)od_text_box_label = tk.Label(interaction_frame, text = 'OD')od_text_box_label.grid(column=2,row=5, sticky='', padx=5, pady=5)odtol_text_box_label = tk.Label(interaction_frame, text = 'OD Tolernace')odtol_text_box_label.grid(column=3,row=5, sticky='', padx=5, pady=5)runout_text_box_label = tk.Label(interaction_frame, text = 'Geometric Tolerane')runout_text_box_label.grid(column=4,row=5, sticky='', padx=5, pady=5)#it is a startcanvas.create_rectangle(20,50,111,150, fill='red', width =3)root.mainloop()