#Here we gooooimport tkinter as tkimport turtlefrom tkinter import ttk# Initiating the windowroot = tk.Tk()# making it so the window is not resizableroot.resizable(width=True, height=True)# This sets the size of the windowroot.geometry('800x600')root.configure(bg='gray')# Creating a 1X2 grid matrix for the main windowsroot.grid_rowconfigure(0, weight=1)root.grid_columnconfigure(0, weight=1)root.grid_columnconfigure(1, weight=1)#Creating the canvas frame and placing it on the left side of the window (first column)canvas_frame = tk.Frame(root, width=400, height=600)canvas_frame.grid(column=0,row=0, sticky='NESW', padx=5, pady=5)#Creating the interaction frame and placing it in the right window (second column)interaction_frame =tk.Frame(root, width=400, height=600)interaction_frame.grid(column=1,row=0, sticky='NESW', padx=5, pady=5)# Creating a canvas, placing it in the frame and set background colorcanvas = tk.Canvas(canvas_frame, bg = 'white', width= 400, height = 600)canvas.grid(column=0,row=0, sticky='NESW')#configuring the interation frameinteraction_frame.grid_columnconfigure(0, weight =1)interaction_frame.grid_columnconfigure(1, weight =1)interaction_frame.grid_columnconfigure(2, weight =1)interaction_frame.grid_columnconfigure(3, weight =1)interaction_frame.grid_columnconfigure(4, weight =1)interaction_frame.grid_rowconfigure(0, weight =1)interaction_frame.grid_rowconfigure(1, weight =1)interaction_frame.grid_rowconfigure(3, weight =1)interaction_frame.grid_rowconfigure(4, weight =1)interaction_frame.grid_rowconfigure(5, weight =1)interaction_frame.grid_rowconfigure(6, weight =1)interaction_frame.grid_rowconfigure(7, weight =1)interaction_frame.grid_rowconfigure(8, weight =1)interaction_frame.grid_rowconfigure(9, weight =1)def draw_part(x,y):    pass#Creating buttonbutton_draw = tk.Button(interaction_frame,                         text = "Draw Part",                          highlightbackground='gray',                         command=draw_part)#placing button on canvasbutton_draw.grid(column=2,row=9, sticky='NESW', padx=5, pady=5)#Creating Material Drop Down Boxmaterial = tk.StringVar()material_chosen = ttk.Combobox(interaction_frame,                                state="readonly",                               width = 20,                             textvariable = material,                            justify = 'center'                            )material_chosen.grid(column=1,row=1, sticky='NESW', padx=5, pady=5)  #Material drop down listmaterial_chosen['values'] = (' Ceramic',                           ' Kovar',                          ' Stainless Steel',                          ' Monel',                          ' May',                          ' June',                             )material_chosen.current(0)text_box = tk.Text(interaction_frame, height = 5, width = 15)text_box.grid(column=1,row=6, sticky='NESW', padx=5, pady=5)#it is a startcanvas.create_rectangle(20,50,111,150, fill='red', width =3)root.mainloop()